import React, {useState} from 'react'
import { BlogPreview, BlogInput } from '../../Components/index'
import './createpage.css'
import './createpage.css'

const CreatePage = () => {
  const [blogContent, setBlogContent] = useState({
    title: "",
    subtitle: "",
    banner: "",
    content:"",
  })
  const postBlog = (blogData) => {
    console.log("input blog", blogData)
    //post api
  }

  return (
    <>
        <h1>CREATE BLOGS PAGE</h1>
        <BlogPreview content={blogContent} />
        <BlogInput setContent={setBlogContent} content={blogContent} />
        <button onClick={()=>postBlog(blogContent)}>SAVE</button>
    </>
  )
}

export {CreatePage}
